INSERT INTO `beck_depression` (`id`, `question`, `answer`, `value`) VALUES
(1, 'Выберите утверждение:', 'Я не чувствую себя расстроенным, печальным.|Я расстроен.|Я все время расстроен и не могу от этого отключиться.|Я настолько расстроен и несчастлив, что не могу это выдержать.', '0|1|2|3'),
(2, 'Выберите утверждение:', 'Я не тревожусь о своем будущем.|Я чувствую, что озадачен будущим.|Я чувствую, что меня ничего не ждет в будущем.|Мое будущее безнадежно, и ничто не может измениться к лучшему.', '0|1|2|3'),
(3, 'Выберите утверждение:', 'Я не чувствую себя неудачником.|Я чувствую, что терпел больше неудач, чем другие люди.|Когда я оглядываюсь на свою жизнь, я вижу в ней много неудач|Я чувствую, что как личность я - полный неудачник.', '0|1|2|3'),
(4, 'Выберите утверждение:', 'Я получаю столько же удовлетворения от жизни, как раньше.|Я не получаю столько же удовлетворения от жизни, как раньше.|Я больше не получаю удовлетворения ни от чего.|Я полностью не удовлетворен жизнью и мне все надоело.', '0|1|2|3'),
