INSERT INTO `sexuality_test_ch` (`id`, `question`, `answer`, `value`) VALUES
(1, 'Какой возраст является для мужчины пиком сексуальности?', 'От 17 до 21 года.|От 21 до 30 лет.|От 30 до 45 лет.', '5|0|0'),
(2, 'В каком возрасте у женщины бывает наиболее сильное сексуальное влечение?', 'От 17 до 21 года.|От 21 до 30 лет.|От 30 до 45 лет.', '0|0|5'),
(3, 'Проявляется ли половое влечение в детстве?', 'Нет.|Только у мальчиков-подростков.|Да.', '0|0|5'),
(4, 'Что играет наиболее существенную роль в формировании твоих сексуальных взглядов?', 'Мнения родителей|Взгляды друзей и подруг.|Только собственный опыт.', '0|0|5'),
(5, 'Что определяет сексуальные потребности человека?', 'Физические и психологические особенности.|Наследственность.|Красота.', '5|0|0'),
(6, 'Каким по твоему мнению, является идеальное отношение к сексу', 'Сексуальные переживания могут быть и без любви.|Любовь необязательно должна привести к сексуальным отношениям.|Любовь и секс неразделимы, вместе они являются основой для полноценных отношений.', '0|0|5'),
(7, 'Как должна поступать женщина, не ощущающая сексуальных потребностей?', 'Ей нужно изменить свое отношение к сексу.|Ей следует исключить секс из своей жизни.|Ей необходимо делать вид, что ей это нравится.', '5|0|0'),
(8, 'Каким прогнозом на будущее является полный страсти медовый месяц?', 'Мужчина и женщина будут вместе до конца своих дней.|Это еще не гарантия семейного счастья.|Это неплохой прогноз.', '0|0|5'),
(9, 'Что является убедительным признаком семейной гармонии?', 'Взаимная нежность, любовь и за порогом спальни.|Интенсивность наивысших сексуальных переживаний.|Полное равнодушие друг к другу.', '5|0|0'),
(10, 'Что предпринять женщине, если ее не удовлетворяет партнер?', 'Быть с ним нежнее.|Не думать об этом.|Поговорить с ним об этом.', '0|0|5'),
(11, 'Насколько безопасны так называемые безопасные дни?', 'Абсолютно безопасны.|Безопасны на 80 %.|Небезопасны.', '0|5|0'),
(12, 'Насколько безопасны оральные контрацептивы?', 'На 85 %.|На 99 %|На 100 %.', '0|5|0'),
(13, 'Ты согласна, что зачатие возможно только в том случае, когда мужчина испытывает оргазм?', 'Да.|Нет.|Не могу ответить', '0|5|0'),
(14, 'Всегда ли женщина должна быть готова к сексу?', 'Конечно, всегда.|Нет.|Всегда, когда она и ее партнер хотят этого.', '0|0|5'),
(15, 'Может ли забеременеть девушка-подросток?', 'Да.|Нет.|Не могу ответить', '5|0|0'),
(16, 'Когда нужно надевать презерватив?', 'Непосредственно перед половым актом на эрегированный пенис.|За 30–40 минут до начала полового акта.|Только на пенис, находящийся в спокойном состоянии.', '5|0|0'),
(17, 'Что вызывает отсутствие сексуального интереса у мужчины?', 'Физическая усталость.|Умственное перенапряжение.|Плохое зрение.', '0|5|0'),
(18, 'Что является причиной импотенции у мужчин, по твоему мнению?', 'Маленький пенис.|Стресс.|Гомосексуализм.', '0|5|0'),
(19, 'Что представляет собой утренняя эрекция у мужчин?', 'Признак серьезного заболевания.|Признак гиперсексуальности.|Нормальное явление.', '0|0|5'),
(20, 'Вредна ли мастурбация в подростковом возрасте?', 'Несомненно да, так как может привести к тяжелому заболеванию.|Она не приносит ни пользы, ни вреда.|Невредна, так как снимает сексуальное напряжение.', '0|0|5'),
(21, 'В чем, по-твоему, заключается счастье женщины в любовном переживании?', 'В полном понимании потребностей своего партнера.|В приобретении опыта в области секса.|В искренней радости от общения с партнером и физической близости с ним', '0|0|5'),
(22, 'Какое высказывание, по-твоему, является верным?', 'Мужчина любит языком и губами, а женщина — руками.|Мужчина любит глазами и руками, а женщина — ушами.|Мужчина любит ушами, а женщина — глазами и руками.', '5|5|0'),
(23, 'Что не относится к средствам предохранения?', 'Внутриматочная спираль.|Оральные контрацептивы.|Гигиенический тампон.', '0|0|5'),
(24, 'В каком случае наступить сексуальная гармония?', 'Если знать себя самого и учитывать лишь собственные желания.|Если понимать партнера и учитывать только его желания.|Если помогать друг другу разбираться в своих желаниях.', '0|0|5'),
(25, 'С кем лучше всего поговорить о своих сексуальных проблемах?', 'С бабушкой и дедушкой.|С приятелями.|Со своим партнером (партнершей).', '0|0|5');
